var db = flow.get("db");

var aio = (x) => {

    var key = x.topic;
    var value = x.payload;
    switch (x.command) {
        case "create": {
            // Create a new database
            flow.set("db", new replitDatabase());
            node.send(x);
            break;
        }
        case "set": {
            // Set a key to a value
            db.set(key, value).then(() => { 
                node.send(x);
            });
            break;
        }
        case "get": {
            // Get a key's value
            db.get(key).then(value => { 
                x.payload = value;
                node.send(x);
            });
            break;
        }
        case "delete": {
            // Delete a key
            db.delete(key).then(() => { 
                node.send(msg);
            });
            break;
        }
        case "keys": {
            if (x.prefix) {
                //List keys with a prefix
                db.list(x.prefix).then(matches => {
                    x.payload = matches;
                    node.send(x);
                });
            } else {
            // List all keys
                db.list().then(keys => { 
                    x.payload = keys;
                    node.send(x);
                });
            }
            break;
        }
    }
}

aio(msg);

return {payload: db};

